param ([String]$BigDocsPath=".",        [long]$MinimumSize="0")  function Get-Docs {     param ([String]$DocsPath=".")     Get-ChildItem -Path $DocsPath `         -Include "*.txt","*.doc","*.docx","*.pdf","*.xls","*.csv","*.ppt","*.pptx","*.ps1" `         -Recurse `         -ErrorAction SilentlyContinue     }  Get-Docs -DocsPath $BigDocsPath |     Where-Object length -ge $MinimumSize |     Select-Object FullName, LastAccessTime, Length |     Sort-Object -Descending Length |     Out-GridView